import { __decorate } from "tslib";
import { Component } from '@angular/core';
let EmployeeComponent = class EmployeeComponent {
    constructor(service, router, route, orderService) {
        this.service = service;
        this.router = router;
        this.route = route;
        this.orderService = orderService;
    }
    async ngOnInit() {
        (await this.service.getEmployee(this.route.snapshot.params.id)).subscribe((data) => {
            this.employee = data;
        });
        (await this.orderService.getOrders()).subscribe((data) => {
            this.orders = data;
        });
        // this.employee = await this.getEmployee(this.route.snapshot.params.id);
        // this.orders = await this.getOrders();
        this.employeeOrders = this.orders.filter(order => order.employeeId === this.employee.id);
    }
    // async getEmployee(id: string) {
    //   return (await this.service.getEmployee(id)).subscribe((data: IEmployee) => {
    //     return data;
    //   });
    // }
    // async getOrders(){
    //   return (await this.orderService.getOrders()).subscribe((data: IOrder[]) => {
    //     return data;
    //   });
    // }
    showOrder(id) {
        this.router.navigate(['/order/' + id]);
    }
};
EmployeeComponent = __decorate([
    Component({
        selector: 'app-employee',
        templateUrl: './employee.component.html',
        styleUrls: ['./employee.component.css']
    })
], EmployeeComponent);
export { EmployeeComponent };
