import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
let AddOrderComponent = class AddOrderComponent {
    constructor(service, router, employeeService, clientService) {
        this.service = service;
        this.router = router;
        this.employeeService = employeeService;
        this.clientService = clientService;
        this.keywordEmployee = 'firstName';
        this.keywordClient = 'carNumber';
    }
    async ngOnInit() {
        this.clients = await this.clientService.getClients();
        this.employees = await this.employeeService.getEmployees();
        this.getEmployeeNames();
        this.getClientCarNumbers();
        this.form = new FormGroup({
            carNumber: new FormControl('', Validators.required),
            employeeName: new FormControl('', Validators.required),
            price: new FormControl(60000, Validators.required),
        });
    }
    selectEventClient(item) {
        // do something with selected item
    }
    selectEventEmployee(item) {
        // do something with selected item
    }
    async getEmployeeNames() {
        this.optionsEmployee = await this.employeeService.getEmployees();
    }
    async getClientCarNumbers() {
        this.optionsClient = await this.clientService.getClients();
    }
    async onSubmit() {
        await this.service.createOrder(this.form.value);
        this.router.navigate(["/orders"]);
    }
};
AddOrderComponent = __decorate([
    Component({
        selector: 'app-add-order',
        templateUrl: './add-order.component.html',
        styleUrls: ['./add-order.component.css']
    })
], AddOrderComponent);
export { AddOrderComponent };
