import { map } from 'rxjs/operators';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
export class OrderService {
    constructor(http) {
        this.http = http;
        this._baseUrl = 'https://localhost:5001/api/orders';
    }
    async getOrders() {
        return this.http.get(`${this._baseUrl}`).pipe(map((data) => {
            return data.data;
        }));
    }
    async getOrder(orderId) {
        return this.http.get(`${this._baseUrl}/${orderId}`).pipe(map((data) => {
            return data.data;
        }));
    }
    async createOrder(order) {
        return this.http.post(this._baseUrl, order).pipe(map((data) => {
            return data.data;
        }));
    }
    updateOrder(order, id) {
        return this.http.put(`${this._baseUrl}/${id}`, order).pipe(map((data) => {
            return data.data;
        }));
    }
    deleteOrder(id) {
        return this.http.delete(`${this._baseUrl}/${id}`).pipe(map((data) => {
            return data.data;
        }));
    }
}
