        return this.http.put(`${this._baseUrl}/${id}`, order).pipe(map((data) => {
            return data.data;
        }));
    }
    async deleteOrder(id) {
        return this.http.delete(`${this._baseUrl}/${id}`).pipe(map((data) => {
            return data.data;
        }));
    }
}
