        this._baseUrl = 'https://localhost:5001/api/employees';
    }
    // async getEmployees() : Promise<Observable<IEmployee[]>> {
    //   return this.http.get(`${this._baseUrl}`).pipe(map((data: any) => {
    //     return data.data;
