        return this.http.get(`${this._baseUrl}/${employeeId}`).toPromise().then((data) => {
            return data.data;
        }).catch(err => {
            console.log(err);
        });
    }
    // async createEmployee(employee: IEmployee) : Promise<Observable<IEmployee>> {
    //   return this.http.post(this._baseUrl, employee).pipe(map((data: any) => {
    //     return data.data;
    //   }));
    // }
    async updateEmployee(employee, id) {
        return this.http.put(`${this._baseUrl}/${id}`, employee).pipe(map((data) => {
            return data.data;
        }));
    }
    async deleteEmployee(id) {
        return this.http.delete(`${this._baseUrl}/${id}`).pipe(map((data) => {
            return data.data;
        }));
    }
    async getData() {
        return this.http.get(this._baseUrl).pipe(map((data) => {
            console.log(data.data);
            return data.data.map((item) => item.firstName);
