import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Order } from 'src/app/interfaces/order';
let AddOrderComponent = class AddOrderComponent {
    constructor(service, router, employeeService, clientService) {
        this.service = service;
        this.router = router;
        this.employeeService = employeeService;
        this.clientService = clientService;
        this.optionsClient = ["Sam", "Varun", "Jasmine"];
        this.optionsEmployee = ["Sam", "Varun", "Jasmine"];
    }
    async ngOnInit() {
        this.clients = await this.clientService.getClients();
        this.employees = await this.employeeService.getEmployees();
        this.getEmployeeNames();
        this.getClientCarNumbers();
        this.initForm();
        this.getOrder();
    }
    initForm() {
        this.form = new FormGroup({
            'carNumber': new FormControl('', Validators.required),
            'employeeName': new FormControl('', Validators.required),
            'price': new FormControl(60000, Validators.required),
        });
        this.form.get('employeeName').valueChanges.subscribe(response => {
            this.filterDataEmployee(response);
        });
        this.form.get('carNumber').valueChanges.subscribe(response => {
            this.filterDataClient(response);
        });
    }
    getOrder() {
        let clientId = this.clients.find(client => client.carNumber === this.form.get('carNumber').value).id;
        let employeeId = this.employees.find(employee => employee.firstName === this.form.get('employeeName').value).id;
        this.order = new Order(this.form.get('price').value, clientId, employeeId);
        return Promise.resolve(this.order);
    }
    filterDataClient(enteredData) {
        this.filteredOptionsClient = this.optionsClient.filter(item => {
            return item.toLowerCase().indexOf(enteredData.toLowerCase()) > -1;
        });
    }
    filterDataEmployee(enteredData) {
        this.filteredOptionsEmployee = this.optionsEmployee.filter(item => {
            return item.toLowerCase().indexOf(enteredData.toLowerCase()) > -1;
        });
    }
    async getEmployeeNames() {
        this.optionsEmployee = await this.employeeService.getData();
        this.filteredOptionsEmployee = this.optionsEmployee;
    }
    async getClientCarNumbers() {
        this.optionsClient = await this.clientService.getData();
        this.filteredOptionsClient = this.optionsClient;
    }
    async onSubmit() {
        await this.service.createOrder(this.order);
        this.router.navigate(["/orders"]);
    }
};
